import React, { useState } from "react";
import "../styles/quiz.css";
import { Link } from "react-router-dom";

export const Quiz = () => {
  const questions = [
    {
      questionText: "When was James Webb Space Telescope launched?",
      answerOptions: [
        { answerText: "20 December, 2021", isCorrect: false },
        { answerText: "18 December, 2021", isCorrect: false },
        { answerText: "25 December, 2021", isCorrect: true },
        { answerText: "15 December, 2021", isCorrect: false },
      ],
    },
    {
      questionText: "Which rocket launched James Webb Space Telescope?",
      answerOptions: [
        { answerText: "Jeff Bezos", isCorrect: false },
        { answerText: "Ariane 5", isCorrect: true },
        { answerText: "Bill Gates", isCorrect: false },
        { answerText: "Tony Stark", isCorrect: false },
      ],
    },
    {
      questionText: "The iPhone was created by which company?",
      answerOptions: [
        { answerText: "Apple", isCorrect: true },
        { answerText: "Intel", isCorrect: false },
        { answerText: "Amazon", isCorrect: false },
        { answerText: "Microsoft", isCorrect: false },
      ],
    },
    {
      questionText: "How many Harry Potter books are there?",
      answerOptions: [
        { answerText: "1", isCorrect: false },
        { answerText: "4", isCorrect: false },
        { answerText: "6", isCorrect: false },
        { answerText: "7", isCorrect: true },
      ],
    },
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);
  const getNumber = () => {
    for (var a = [0, 1, 2, 3], i = a.length; i--; ) {
      var random = a.splice(Math.floor(Math.random() * (i + 1)), 1)[0];
      var number = random;
    }
    return number;
  };
  const handleAnswerOptionClick = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    const nextQuestion = currentQuestion + 1;

    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };
  return (
    <div className="app">
      <div className="quiz">
        {showScore ? (
          <div className="score-section">
            You scored {score} out of {questions.length}
          </div>
        ) : (
          <>
            <div className="question-section">
              <div className="question-count">
                <span>Question {getNumber() + 1}</span>/{questions.length}
              </div>
              <div className="question-text">
                {questions[getNumber()].questionText}
              </div>
            </div>
            <div className="answer-section">
              {questions[getNumber()].answerOptions.map((answerOption) => (
                <button
                  onClick={() =>
                    handleAnswerOptionClick(answerOption.isCorrect)
                  }
                >
                  {answerOption.answerText}
                </button>
              ))}
            </div>
          </>
        )}
      </div>
      <div className="links">
        <Link to="/puzzle-game">PuzzleGame</Link>
        <Link to="/quiz">New Quiz</Link>
      </div>
    </div>
  );
};
