import React, { useState } from "react";
import "../styles/quiz.css";
import { Link } from "react-router-dom";

export const Quiz = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [score, setScore] = useState(0);
  const getNumber = () => {
    console.log(questions);
    var random = Math.floor(Math.random() * (questions.length + 1));
    var number = random;

    questions = questions.filter((question) => question.id != number);
    console.log(questions, number);
    return number;
  };

  getNumber();

  const handleAnswerOptionClick = (isCorrect) => {
    if (isCorrect) {
      setScore(score + 1);
    }
    const nextQuestion = currentQuestion + 1;

    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };
  return (
    <div className="app">
      <div className="quiz">
        {showScore ? (
          <div className="score-section">
            You scored {score} out of {questions.length}
          </div>
        ) : (
          <>
            <div className="question-section">
              <div className="question-count">
                <span>Question {1}</span>/{questions.length}
              </div>
              <div className="question-text">{questions[1].questionText}</div>
            </div>
            <div className="answer-section">
              {questions[1].answerOptions.map((answerOption) => (
                <button
                  onClick={() =>
                    handleAnswerOptionClick(answerOption.isCorrect)
                  }
                >
                  {answerOption.answerText}
                </button>
              ))}
            </div>
          </>
        )}
      </div>
      <div className="links">
        <Link to="/puzzle-game">PuzzleGame</Link>
        <Link to="/quiz">New Quiz</Link>
      </div>
    </div>
  );
};
